// RSA Non Coprime Moduli

let c1 = "";

let c2 = "";

let n1 = "";

let n2 = "";

let p1 = bignum_gcd(n1, n2);
print("p1:" +p1+"\n");

let p2 = bignum_div(n1, p1);
print("p2:" +p2+"\n");

let p3 = bignum_div(n2, p1);
print("p2:" +p3+"\n");

let e = "0x10001";

let d1 = bignum_invert(e, bignum_mul(bignum_sub(p1, "1"), bignum_sub(p2, "1")));
print("d1:" +d1+"\n");

let d2 = bignum_invert(e, bignum_mul(bignum_sub(p1, "1"), bignum_sub(p3, "1")));
print("d2:" +d2+"\n");

let m1 = bignum_powm(c1, d1, n1);
print("m1:" +m1+"\n");

let m2 = bignum_powm(c2, d2, n2);
print("m2:" +m2+"\n");

let plain_text = bignum_convert_string(m1+m2);
print("plain_text: "+plain_text+"\n");
